import "./App.css";

function App() {
  return (
    <form name="calc" className="calculator">
      <input
        type="text"
        placeholder="0"
        name="txt"
        className="value"
        readonly=""
      />
      <span onClick={() => (document.calc.txt.value += "7")}>7</span>
      <span onClick={() => (document.calc.txt.value += "8")}>8</span>
      <span onClick={() => (document.calc.txt.value += "*")}>*</span>
      <span
        className="oper clear"
        onClick={() => (document.calc.txt.value = "")}
      >
        C
      </span>
      <span
        className="oper equal"
        onClick={(e) => (document.calc.txt.value = eval(e.target.txt.value))}
      >
        =
      </span>
    </form>
  );
}

export default App;
