import "./App.css";
import { useState } from "react";

function App() {
  // const [result, setResult] = useState("");

  // const myFunction = (e) => {
  //   e.preventDefault();
  //   setResult(e.target.value);
  //   eval(setResult);
  //   console.log(e);
  // };

  return (
    <form name="calc" className="calculator">
      <input
        type="text"
        placeholder="0"
        name="txt"
        className="value"
        readonly=""
      />
      <span className="oper" onClick={() => (document.calc.txt.value += "7")}>
        7
      </span>
      <span className="oper" onClick={() => (document.calc.txt.value += "8")}>
        8
      </span>
      <span className="oper" onClick={() => (document.calc.txt.value += "*")}>
        *
      </span>
      <span
        className="oper clear"
        onClick={() => (document.calc.txt.value = "")}
      >
        C
      </span>
      <span
        className="oper equal"
        onClick={() => (document.calc.txt.value = "eval(calc.txt.value)")}
      >
        =
      </span>
    </form>
  );
}

export default App;
